import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { registerUser } from "../../services/api"; // Importa a fun√ß√£o nova
import './cadastro.css';

const Cadastro = () => {
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    nome_completo: "",
    nome_usuario: "",
    email: "",
    data_nascimento: "",
    senha: ""
  });

  const [mensagemErro, setMensagemErro] = useState("");
  const [mostrarSenha, setMostrarSenha] = useState(false);

  const handleChange = (e) => {
    setFormData(prev => ({
      ...prev,
      [e.target.name]: e.target.value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      await registerUser(formData); // Chama a fun√ß√£o da API
      alert('Conta criada com sucesso! Voc√™ ser√° redirecionado para o login.'); // ‚úÖ AVISO AQUI
      navigate('/'); // Redireciona para login ap√≥s clicar OK no alert
    } catch (error) {
      setMensagemErro(error.message || "Erro ao registrar usu√°rio.");
    }
  };

  const handleTogglePassword = () => {
    setMostrarSenha(prev => !prev);
  };

  return (
    <div className="container">
      <h1>Clube da Luta</h1>
      <form onSubmit={handleSubmit} autoComplete="off">
        <label htmlFor="nome_completo">Nome completo</label>
        <input
          type="text"
          id="nome_completo"
          name="nome_completo"
          value={formData.nome_completo}
          onChange={handleChange}
          required
          autoComplete="off"
        />

        <label htmlFor="nome_usuario">Nome de usu√°rio</label>
        <input
          type="text"
          id="nome_usuario"
          name="nome_usuario"
          value={formData.nome_usuario}
          onChange={handleChange}
          required
          autoComplete="off"
        />

        <label htmlFor="email">Email</label>
        <input
          type="email"
          id="email"
          name="email"
          value={formData.email}
          onChange={handleChange}
          required
          autoComplete="off"
        />

        <label htmlFor="data_nascimento">Data de nascimento</label>
        <input
          type="date"
          id="data_nascimento"
          name="data_nascimento"
          value={formData.data_nascimento}
          onChange={handleChange}
          required
          autoComplete="off"
        />

        <label htmlFor="senha">Senha</label>
        <div className="senha-container">
          <input
            type={mostrarSenha ? "text" : "password"}
            id="senha"
            name="senha"
            value={formData.senha}
            onChange={handleChange}
            required
            autoComplete="new-password"
          />
          <span
            className="toggle-password"
            onClick={handleTogglePassword}
          >
            {mostrarSenha ? 'üôà' : 'üëÅÔ∏è'}
          </span>
        </div>

        <button type="submit">Cadastrar</button>

        {mensagemErro && <div className="mensagem-erro">{mensagemErro}</div>}

        <div className="link">
          J√° tem uma conta? <a href="/">Fa√ßa login</a>
        </div>
      </form>
    </div>
  );
};

export default Cadastro;
