// src/services/api.js

const API_URL = 'http:192.168.0.111//:4000/users'; // URL do seu servidor ou endpoint do Supabase

// Função para obter os dados de um usuário pelo ID
export const getUserById = async (id) => {
  try {
    const response = await fetch(`${API_URL}/${id}`);
    if (!response.ok) {
      throw new Error('Erro ao recuperar usuário');
    }
    return await response.json();
  } catch (error) {
    console.error(error);
    throw error;
  }
};

// Função para atualizar os dados do usuário
export const updateUser = async (id, updatedData) => {
  try {
    const response = await fetch(`${API_URL}/${id}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(updatedData),
    });

    if (!response.ok) {
      throw new Error('Erro ao atualizar usuário');
    }

    return await response.json(); // Retorna os dados atualizados
  } catch (error) {
    console.error(error);
    throw error;
  }
};

// Função para deletar um usuário
export const deleteUser = async (id) => {
  try {
    const response = await fetch(`${API_URL}/${id}`, {
      method: 'DELETE',
    });

    if (!response.ok) {
      throw new Error('Erro ao excluir usuário');
    }

    return await response.json();
  } catch (error) {
    console.error(error);
    throw error;
  }
};
