import { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./cadastro.css";

export default function Cadastro() {
  const navigate = useNavigate();

  const [form, setForm] = useState({
    name: "",
    data_nascimento: "",
    email: "",
    username: "",
    password: ""
  });

  const [mensagem, setMensagem] = useState("");

  function handleChange(e) {
    setForm({ ...form, [e.target.name]: e.target.value });
  }

  async function handleSubmit(e) {
    e.preventDefault();
    setMensagem("");

    try {
      const response = await fetch("http://localhost:4000/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          nome_completo: form.name,
          data_nascimento: form.data_nascimento,
          email: form.email,
          nome_usuario: form.username,
          password: form.password
        })
      });

      const data = await response.json();

      if (response.ok) {
        setMensagem("🔥 Cadastro realizado com sucesso! Agora você faz parte do Clube da Luta.");
        setForm({ name: "", data_nascimento: "", email: "", username: "", password: "" });

        setTimeout(() => navigate("/login"), 2000);
      } else {
        setMensagem(`❌ ${data.error || "Erro no cadastro."}`);
      }
    } catch (err) {
      setMensagem("❌ Erro ao conectar com o servidor.");
    }
  }

  return (
    <div className="container">
      <h1>Cadastro</h1>
      <form onSubmit={handleSubmit}>
        <label htmlFor="name">Nome completo</label>
        <input name="name" type="text" value={form.name} onChange={handleChange} autoComplete="off"
 required />

        <label htmlFor="data_nascimento">Data de nascimento</label>
        <input name="data_nascimento" type="date" value={form.data_nascimento} onChange={handleChange} required />

        <label htmlFor="email">E-mail</label>
        <input name="email" type="email" value={form.email} onChange={handleChange} required />

        <label htmlFor="username">Nome de usuário</label>
        <input name="username" type="text" value={form.username} onChange={handleChange} required />

        <label htmlFor="password">Senha</label>
        <div className="senha-container">
          <input
            name="password"
            type="password"
            value={form.password}
            onChange={handleChange}
            required
          />
        </div>

        <button type="submit">Cadastrar</button>
        {mensagem && (
          <p className="mensagem-erro" style={{ marginTop: "1rem" }}>
            {mensagem}
          </p>
        )}
        <p className="link">Já tem uma conta? <a href="/login">Entrar</a></p>
      </form>
    </div>
  );
}
