import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './menu.css';
import { ReactComponent as SearchIcon } from '../assets/icons/search.svg'; // exemplo
import { ReactComponent as PostIcon } from '../assets/icons/post.svg';
// Adicione seus próprios SVGs em assets/icons/

function Menu() {
  const [isMenuActive, setIsMenuActive] = useState(false);
  const [isSidebarCollapsed, setIsSidebarCollapsed] = useState(true);

  useEffect(() => {
    const handleResize = () => {
      setIsSidebarCollapsed(window.innerWidth < 1024);
    };

    window.addEventListener('resize', handleResize);
    handleResize(); // inicializa corretamente

    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const toggleSidebar = () => setIsSidebarCollapsed(!isSidebarCollapsed);
  const toggleMenu = () => setIsMenuActive(!isMenuActive);

  return (
    <aside className={`sidebar ${isSidebarCollapsed ? 'collapsed' : ''} ${isMenuActive ? 'menu-active' : ''}`}>
      <header className="sidebar-header">
        <button className="toggler sidebar-toggler" onClick={toggleSidebar}>⮜</button>
        <button className="toggler menu-toggler" onClick={toggleMenu}>
          {isMenuActive ? '✖' : '☰'}
        </button>
      </header>

      <nav className="sidebar-nav">
        <ul className="nav-list primary-nav">
          <li className="nav-item">
            <Link to="/search" className="nav-link">
              <SearchIcon className="nav-icon" />
              <span className="nav-label">Pesquisar</span>
            </Link>
            <span className="nav-tooltip">Pesquisar</span>
          </li>
          <li className="nav-item">
            <Link to="/feed" className="nav-link">
              <PostIcon className="nav-icon" />
              <span className="nav-label">Feed</span>
            </Link>
            <span className="nav-tooltip">Feed</span>
          </li>
          {/* Continue com os outros links usando SVGs */}
        </ul>

        <ul className="nav-list secondary-nav">
          <li className="nav-item">
            <Link to="/profile" className="nav-link">
              {/* SVG do perfil */}
              <span className="nav-label">Perfil</span>
            </Link>
            <span className="nav-tooltip">Perfil</span>
          </li>
          <li className="nav-item">
            <Link to="/logout" className="nav-link">
              {/* SVG do logout */}
              <span className="nav-label">Sair</span>
            </Link>
            <span className="nav-tooltip">Sair</span>
          </li>
        </ul>
      </nav>
    </aside>
  );
}

export default Menu;
