import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { registerUser } from "../../services/api"; // Importa a fun칞칚o nova
import './cadastro.css';

const Cadastro = () => {
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    nome_completo: "",
    nome_usuario: "",
    email: "",
    senha: ""
  });

  const [mensagemErro, setMensagemErro] = useState("");

  const handleChange = (e) => {
    setFormData(prev => ({
      ...prev,
      [e.target.name]: e.target.value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      await registerUser(formData); // Chama o registerUser
      navigate('/'); // Redireciona para login ap칩s cadastro
    } catch (error) {
      setMensagemErro(error.message || "Erro ao registrar usu치rio.");
    }
  };

  const handleTogglePassword = () => {
    const inputSenha = document.getElementById("senha");
    if (inputSenha.type === "password") {
      inputSenha.type = "text";
    } else {
      inputSenha.type = "password";
    }
  };

  return (
    <div className="container">
      <h1>Clube da Luta</h1>
      <form onSubmit={handleSubmit} autoComplete="off">
        <label htmlFor="nome_completo">Nome completo</label>
        <input
          type="text"
          id="nome_completo"
          name="nome_completo"
          value={formData.nome_completo}
          onChange={handleChange}
          required
          autoComplete="off"
        />

        <label htmlFor="nome_usuario">Nome de usu치rio</label>
        <input
          type="text"
          id="nome_usuario"
          name="nome_usuario"
          value={formData.nome_usuario}
          onChange={handleChange}
          required
          autoComplete="off"
        />

        <label htmlFor="email">Email</label>
        <input
          type="email"
          id="email"
          name="email"
          value={formData.email}
          onChange={handleChange}
          required
          autoComplete="off"
        />

        <label htmlFor="senha">Senha</label>
        <div className="senha-container">
          <input
            type="password"
            id="senha"
            name="senha"
            value={formData.senha}
            onChange={handleChange}
            required
            autoComplete="new-password"
          />
          <span
            className="toggle-password"
            onClick={handleTogglePassword}
          >
            游녜勇끂r
          </span>
        </div>

        <button type="submit">Cadastrar</button>

        {mensagemErro && <div className="mensagem-erro">{mensagemErro}</div>}

        <div className="link">
          J치 tem uma conta? <a href="/">Fa칞a login</a>
        </div>
      </form>
    </div>
  );
};

export default Cadastro;
