import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './SidebarMenu.css';

function Menu() {
  const [isCollapsed, setIsCollapsed] = useState(window.innerWidth < 1024);

  useEffect(() => {
    const handleResize = () => setIsCollapsed(window.innerWidth < 1024);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const toggleMenu = () => {
    setIsCollapsed(!isCollapsed);
  };

  return (
    <aside className={`sidebar ${isCollapsed ? 'collapsed' : ''}`}>
      <div className="sidebar-header">
        <h2 className="logo">CLUBE</h2>
        <button className="toggle-btn" onClick={toggleMenu}>
          {isCollapsed ? '☰' : '⮜'}
        </button>
      </div>

      <nav className="nav-links">
        <Link to="/feed" className="nav-item">Feed</Link>
        <Link to="/search" className="nav-item">Pesquisar</Link>
        <Link to="/ranking" className="nav-item">Ranking</Link>
        <Link to="/avisos" className="nav-item">Avisos</Link>
        <Link to="/global_chat" className="nav-item">Chat Global</Link>
        <Link to="/training" className="nav-item">Treinos</Link>
        <Link to="/locais" className="nav-item">Locais</Link>
        <Link to="/rules" className="nav-item">Regras</Link>
        <div className="nav-bottom">
          <Link to="/profile" className="nav-item">Perfil</Link>
          <Link to="/logout" className="nav-item">Sair</Link>
        </div>
      </nav>
    </aside>
  );
}

export default SidebarMenu;
