import { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./login.css";

function Login() {
  const [emailOrUsername, setEmailOrUsername] = useState("");
  const [password, setPassword] = useState("");
  const [mostrarSenha, setMostrarSenha] = useState(false);
  const [mensagem, setMensagem] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    const resposta = await fetch("http://localhost:3000/login", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ emailOrUsername, password }),
    });

    const data = await resposta.json();

    if (resposta.ok) {
      localStorage.setItem("user", JSON.stringify(data.user));
      navigate("/feed");
    } else {
      setMensagem(data.error || "Erro desconhecido.");
    }
  };

  return (
    <div className="login-container">
      <header>
        <h1>Clube da Luta</h1>
      </header>

      <main>
        <h2>Bem-vindo ao Clube</h2>
        <p>‚ÄúA primeira regra do Clube da Luta √©: Voc√™ n√£o fala sobre o Clube da Luta.‚Äù</p>

        <form onSubmit={handleSubmit} className="login-form">
          <h3>Login</h3>

          <label htmlFor="emailOrUsername">Usu√°rio ou E-mail</label>
          <input
            type="text"
            id="emailOrUsername"
            value={emailOrUsername}
            onChange={(e) => setEmailOrUsername(e.target.value)}
            required
          />

          <label htmlFor="password">Senha</label>
          <div className="senha-container">
            <input
              type={mostrarSenha ? "text" : "password"}
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
            <span
              className="toggle-password"
              onClick={() => setMostrarSenha(!mostrarSenha)}
            >
              üëÅÔ∏è
            </span>
          </div>

          <button type="submit">Entrar</button>
          <p>N√£o tem conta? <a href="/register">Cadastre-se</a></p>
        </form>

        {mensagem && <p className="mensagem-erro">{mensagem}</p>}
      </main>
    </div>
  );
}

export default Login;
