import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { updateUser } from "../../services/api"; // Importando a função de atualização

const EditarPerfil = () => {
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    nome: "",
    nome_usuario: "",
    idade: "",
    peso: "",
    altura: "",
    estilo_luta: []
  });

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem("usuario_info"));
    if (user) {
      setFormData({
        nome: user.nome_completo || "",
        nome_usuario: user.nome_usuario || "",
        idade: user.idade || "",
        peso: user.peso || "",
        altura: user.altura || "",
        estilo_luta: user.estilo_luta || ""
      });
    }
  }, []);

  const handleChange = (e) => {
    setFormData(prev => ({
      ...prev,
      [e.target.name]: e.target.value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const user = JSON.parse(localStorage.getItem("usuario_info"));
    if (!user) return;
  
    const updatedData = {
      nome_completo: formData.nome,
      nome_usuario: formData.nome_usuario,
      idade: formData.idade,
      peso: formData.peso || null,
      altura: formData.altura || null,
      estilo_luta: formData.estilo_luta || null
    };
  
    try {
      await updateUser(user.id, updatedData); // Só manda, ignora a resposta
  
      // Atualiza o localStorage NA MÃO
      const updatedUser = { ...user, ...updatedData }; 
      localStorage.setItem("usuario_info", JSON.stringify(updatedUser));
  
      navigate("/profile"); // Manda pro perfil depois
    } catch (error) {
      console.error("Erro ao atualizar perfil:", error);
      alert("Erro ao atualizar perfil.");
    }
  };
  
  const handleCancel = (e) => {
    e.preventDefault();
    navigate("/profile");
  };

  return (
    <div className="container">
      <h1>Editar Perfil</h1>
      <form id="edit-profile-form" onSubmit={handleSubmit}>
        <label htmlFor="nome">Nome</label>
        <input
          type="text"
          id="nome"
          name="nome"
          value={formData.nome}
          onChange={handleChange}
          required
        />

        <label htmlFor="nome_usuario">Nome de usuário</label>
        <input
          type="text"
          id="nome_usuario"
          name="nome_usuario"
          value={formData.nome_usuario}
          onChange={handleChange}
          required
        />

        <label htmlFor="idade">Idade</label>
        <input
          type="number"
          id="idade"
          name="idade"
          value={formData.idade}
          onChange={handleChange}
          required
        />

        <label htmlFor="peso">Peso (kg)</label>
        <input
          type="number"
          step="0.1"
          id="peso"
          name="peso"
          value={formData.peso}
          onChange={handleChange}
        />

        <label htmlFor="altura">Altura (cm)</label>
        <input
          type="number"
          id="altura"
          name="altura"
          value={formData.altura}
          onChange={handleChange}
        />

        <label htmlFor="estilo_luta">Estilo de luta</label>
        <input
          type="text"
          id="estilo_luta"
          name="estilo_luta"
          value={formData.estilo_luta}
          onChange={handleChange}
        />

        <button type="submit">Salvar alterações</button>
        <button type="button" onClick={handleCancel}>
          Cancelar alterações
        </button>
      </form>
    </div>
  );
};

export default EditarPerfil;
